        [Benchmark]
        public bool Run_ValidMountainArray()
        {
            return ValidMountainArray(this.benchmarkNumbers);
        }

        private static bool ValidMountainArray(int[] arr)
        {
            var asSpan = arr.AsSpan();

            if (asSpan.Length < 3)
            {
                return false;
            }

            bool increasing = asSpan[0] < asSpan[1];

            if (!increasing)
            {
                return false;
            }

            for (int i = 0,
                     j = 1;
                j < asSpan.Length;
                i++, j++)
            {
                if (asSpan[i] == asSpan[j])
                {
                    return false;
                }

                switch (increasing)
                {
                    case false when asSpan[i] < asSpan[j]:
                        return false;

                    case true when asSpan[i] > asSpan[j]:
                        increasing = false;
                        break;
                }
            }

            return !increasing;
        }
    }
